package com.example.converter.ui.home;

import android.graphics.Color;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.NavController;
import androidx.navigation.fragment.NavHostFragment;

import com.example.converter.R;
import com.example.converter.SliderAdapter;
import com.example.converter.SliderItem;
import com.smarteist.autoimageslider.IndicatorView.animation.type.IndicatorAnimationType;
import com.smarteist.autoimageslider.SliderAnimations;
import com.smarteist.autoimageslider.SliderView;

public class HomeFragment extends Fragment {

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View fragmentLayout = inflater.inflate(R.layout.fragment_home, container, false);
        NavController navController = NavHostFragment.findNavController(this);

        SliderView sliderView = fragmentLayout.findViewById(R.id.imageSlider);

        SliderAdapter adapter = new SliderAdapter(getContext());
        SliderItem sliderItem = new SliderItem();
        sliderItem.setImageUrl("https://www.google.ru/url?sa=i&url=https%3A%2F%2Ftwitter.com%2Fptichkalcoholic&psig=AOvVaw2Zc5cYsXxCCXSkUp1mYw4P&ust=1616247848628000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCNi4p8q-vO8CFQAAAAAdAAAAABAD");
        sliderItem.setDescription("asd");
        adapter.addItem(sliderItem);
        SliderItem sliderItem2 = new SliderItem();
        sliderItem.setImageUrl("https://simple-fauna.ru/wp-content/uploads/2016/11/ptichki-amadiny-1.jpg");
        sliderItem.setDescription("asd");
        adapter.addItem(sliderItem2);
        SliderItem sliderItem3 = new SliderItem();
        sliderItem.setImageUrl("data:image/jpeg;base64,");
        sliderItem.setDescription("asd");
        adapter.addItem(sliderItem3);
        sliderView.setSliderAdapter(adapter);

        sliderView.setIndicatorAnimation(IndicatorAnimationType.WORM); //set indicator animation by using IndicatorAnimationType. :WORM or THIN_WORM or COLOR or DROP or FILL or NONE or SCALE or SCALE_DOWN or SLIDE and SWAP!!
        sliderView.setSliderTransformAnimation(SliderAnimations.SIMPLETRANSFORMATION);
        sliderView.setAutoCycleDirection(SliderView.AUTO_CYCLE_DIRECTION_BACK_AND_FORTH);
        sliderView.setIndicatorSelectedColor(Color.WHITE);
        sliderView.setIndicatorUnselectedColor(Color.GRAY);
        sliderView.setScrollTimeInSec(4); //set scroll delay in seconds :
        sliderView.startAutoCycle();


        return fragmentLayout;
    }
}
